import React, { useState, useEffect } from "react";
import { useHistory } from "react-router-dom";
import { apiCreateChannel, apiGetChannel } from "../../utils/api_users"
import { useForm } from 'react-hook-form'

const url = require('url')
const qs = require('querystring')

const Home = (props) => {
    const user = (qs.parse(url.parse(window.location.href).query)['id'])

    const { register, handleSubmit, errors } = useForm()
    const [myErrors, setErrors] = useState('')
    let history = useHistory()

    const logOut = () => {
        history.push('/');
    }

    const onSubmit = (data) => apiCreateChannel(
        user, data.channel, (erreur) => {
            setErrors(erreur)
        });

    const [channels, setChannels] = useState([]);
    const printChannels = () => {
        apiGetChannel(user, (channels) => {
            setChannels(channels)
        })
    }
    useEffect(() => {
        printChannels()
    }, [channels])

    return (
        <form className="base-container" onSubmit= { handleSubmit(onSubmit)} >
            <div className="chan">
              {channels.map(channels)}
            </div>
            <div className="header">Bienvenue retour, {user} !</div>
            <div className="content">
            <div className="form-group">
              <input type="text"
                     name="channel"
                     placeholder="channel"
                     ref={register({ required: true, maxLength: 30 })} />
            </div>
              <button type="submit"
                      className="btn">
                Create a channel
              </button>
            </div>
            {myErrors &&
                <div className="alert-erreur" role="alert">
                    {myErrors}
                </div>
            }
            <div className="log-out-button">
                <span className="register-message" onClick={() => logOut()}>Log Out</span>
            </div>
        </form>
    )
}


export default Home;
