import React from "react";
import { useHistory } from "react-router-dom";
import { apiCreateChannel } from "../../utils/api_users"
import { useForm } from 'react-hook-form'

const url = require('url')
const qs = require('querystring')

const Home = (props) => {
    const user = (qs.parse(url.parse(window.location.href).query)['id'])

    const { register, handleSubmit, errors } = useForm()
    const [myerrors, setErrors] = useState('')
    let history = useHistory()

    const logOut = () => {
        history.push('/');
    }

    const callApi = () => {
        apiCreateChannel(user,
            (erreur) => {
                console.log(erreur)
            })
    }
    const onSubmit = (data) => apiCreateChannel(
        user, data.channel, (erreur) => {
            setErrors(erreur)
        });

    return (
        <div className="base-container" onSubmit= { handleSubmit(onSubmit)} >
            <div className="header">Bienvenue retour, {user} !</div>
            <div className="content">
            <div className="form-group">
              <input type="text"
                     name="channel"
                     placeholder="channel"
                     ref={register({ required: true, maxLength: 30 })} />
            </div>
              <button onClick={() => callApi()}>
                Create a channel
              </button>
            </div>
            <div className="log-out-button">
                <span className="register-message" onClick={() => logOut()}>Log Out</span>
            </div>
        </div>
    )
}


export default Home;
